package week1.day1;

public class OddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=20;i++)
		{
			//To print even num for 1 -20 
			//if(i%2==0)
				
			//To print odd num for 1 -20 
			if(i%2!=0)
			{
				System.out.println(i);
			}
		}

	}

}
